export function geraSequenciaMega(min, max, tamanhoArray) {
    console.log(tamanhoArray)
    return aplicaMascara(ordena(getArray(min, max, tamanhoArray)))
}

function aplicaMascara(array) {
    console.log(array)

    var texto = '';

    for (let i = 0; i < array.length; i++) {
        if (i === 0) {
            texto += ('[' + array[i] + ',')
        } else if (i === array.length - 1) {
            texto += (array[i] + ']')
        } else {
            texto += (array[i] + ',')
        }
    }
    return texto
}

function ordena(array) {
    return array.sort(function (a, b) { return a - b; });
}

function getArray(min, max, qtde) {
    var array = new Set();
    for (let i = 0; i < qtde; i++) {
        array.add(getNumeroRandom(min, max))
    }
    return Array.from(array);
}

function getNumeroRandom(min, max) {
    return parseInt(Math.random() * (max - min) + min);
}

console.log(geraSequenciaMega(1, 60))
