import React, { Component } from "react";

class Contador extends Component {
    state = {
        numero: this.props.numeroInicial
    }

    constructor(props) {
        super(props)

        this.state = {
            numero: props.numeroInicial
        }
    }

    render() {
        return (
            <div>
                <h2>Contador</h2>
                <p>Valor Inicial: { this.state.numero }</p>
            </div>
        )
    }
}

export default Contador
