export function geraSequenciaMega(min, max, tamanhoArray) {
    const tamanhoArrayInt = parseInt(tamanhoArray)
    console.log({tamanhoArrayInt})

    const sequenciaGerada = geraSequencia(min, max, tamanhoArrayInt)    
    return sequenciaGerada    
}

function geraSequencia(min, max, tamanhoArray) {
    const array = getArray(min, max, tamanhoArray)
    console.log(array)
    
    const arrayOrdenado = ordena(array)
    console.log(arrayOrdenado)

    const arrayOrdenadoString = aplicaMascara(arrayOrdenado)
    console.log(arrayOrdenadoString)

    return arrayOrdenadoString
}

function aplicaMascara(array) {
    var texto = '';

    for (let i = 0; i < array.length; i++) {
        if (i === 0) {
            texto += ('[' + array[i] + ',')
        } else if (i === array.length - 1) {
            texto += (array[i] + ']')
        } else {
            texto += (array[i] + ',')
        }
    }
    return texto
}

function ordena(array) {
    return array.sort(function (a, b) { return a - b; });
}

function getArray(min, max, qtde) {
    var array = new Set();
    while(array.size < 9) {
        array.add(getNumeroRandom(min, max))
    }
    return Array.from(array);
}

function getNumeroRandom(min, max) {
    return parseInt(Math.random() * (max - min) + min);
}
