    const [tamanhoArrayDigitado, setTamanhoArrayDigitado] = useState(6)
    const [sequencia, setSequencia] = useState('Vazio')

    function gerarSeq() {
        setSequencia(gerarNumeros(props.min, props.max, tamanhoArrayDigitado))
    }

    function quandoMudar(e) {
        setTamanhoArrayDigitado(e.target.value)
    }

    return (
        <div>
            <h2>NÃºmeros gerados:</h2>{sequencia}
            <h3><input type="number" placeholder="N de valores" 
                value={tamanhoArrayDigitado} onChange={quandoMudar}></input></h3>
            <button onClick={gerarSeq}>Gerar</button>
